package com.example.demo.entity;

import jakarta.persistence.*;
import lombok.*;

/**
 * TPJ_EMPLOYEE 테이블을 표현하는 Entity 클래스입니다.
 * 자동 생성되는 id(Long) 필드를 기본키로 사용합니다.
 * empKey(String, NOT NULL, Unique)는 원본 테이블의 기본키입니다.
 * empName(String, NOT NULL), deptCode(String, NOT NULL) 필드와 함께
 * regularYn(String, NOT NULL) 필드로 구성됩니다.
 */
@Entity
@Table(name = "TPJ_EMPLOYEE")
@Data
public class TpjEmployee {
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    @Column(unique = true, nullable = false, length = 10)
    private String empKey;

    @Column(nullable = false, length = 1)
    private String regularYn;

    @Column(nullable = false, length = 50)
    private String empName;

    @Column(nullable = false, length = 10)
    private String deptCode;
}

